import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./RMFpage.css";

const RMFPage = () => {
  const [categories, setCategories] = useState([
    {
      name: "Clubs",
      subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "Sports"],
      image: "/images/clubs-image.jpg",
    },
    {
      name: "Academics",
      subcategories: ["First Year", "Second Year", "Third Year", "Fourth Year"],
      image: "/images/academic-excellence.jpg",
    },
    {
      name: "Notices",
      subcategories: [],
      image: "/images/notice.jpg",
    },
    {
      name: "Skill Hub",
      subcategories: ["Hackathons", "Coding Contests", "Training Programmes"],
      image: "/images/skill-hub.jpg",
    },
    {
      name: "Events",
      subcategories: [],
      image: "/images/events-icon.jpg",
    },
    {
      name: "Placements",
      subcategories: [],
      image: "/images/placements.jpg",
    },
  ]);

  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState(null);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/rmfposts");
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []);

  return (
    <div className="rmf-page">
      <h1>RMF Page</h1>

      {/* Categories Section */}
      <div className="categories">
        {categories.map((category) => (
          <div
            key={category.name}
            className="category"
            onMouseEnter={() => setActiveCategory(category.name)}
            onMouseLeave={() => setActiveCategory(null)}
          >
            <img src={category.image} alt={`${category.name} Icon`} className="category-image" />
            <h2>{category.name}</h2>
            {activeCategory === category.name && category.subcategories.length > 0 && (
              <ul className="subcategories">
                {category.subcategories.map((sub, idx) => (
                  <li key={idx}>
                    {sub === "RMF" ? (
                      <Link to="/rmfpage">{sub}</Link>
                    ) : (
                      sub
                    )}
                  </li>
                ))}
              </ul>
            )}
          </div>
        ))}
      </div>

      {/* Feed Section */}
      <div className="feed">
        <h2>RMF Feed</h2>
        <div className="feed-grid">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts.map((post, index) => (
              <div className="feed-item" key={post._id}>
                <div className="feed-image">
                  {post.media.length > 0 && (
                    <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                      {post.media.map((media, idx) => (
                        <div key={idx}>
                          {media.type.startsWith("image/") ? (
                            <img src={media.url} alt={`Post ${index} Image ${idx}`} />
                          ) : media.type === "application/pdf" ? (
                            <iframe
                              src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                              title="PDF Viewer"
                              width="100%"
                              height="500px"
                              style={{ border: "none" }}
                            ></iframe>
                          ) : media.type === "video/mp4" ? (
                            <video src={media.url} controls style={{ width: "100%", height: "auto" }} />
                          ) : null}
                        </div>
                      ))}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a href={post.link} target="_blank" rel="noopener noreferrer">
                      {post.link}
                    </a>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default RMFPage;
