import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Or use axiosInstance if configured
import "./Login.css";

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send request to backend to authenticate
      const response = await axios.post("/login", { email, password });

      // Check for role in response and call onLogin
      const { role } = response.data;
      if (role === "student" || role === "coordinator") {
        onLogin(role); // Pass the role dynamically
      } else {
        throw new Error("Invalid role assigned to user.");
      }
    } catch (err) {
      console.error("Login error:", err); // Log error to console
      setError(err.response ? err.response.data.message : "Server error");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
