import React, { useEffect, useState } from "react";
import axios from "axios";
import "./DraftsPage.css";
import { useNavigate } from "react-router-dom";

const Drafts = () => {
  const [drafts, setDrafts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDrafts = async () => {
      try {
        const response = await axios.get("http://localhost:3000/draftsview/draftsget");
        setDrafts(response.data);
      } catch (error) {
        console.error("Error fetching drafts:", error);
      }
    };

    fetchDrafts();
  }, []);

  const handleDelete = async (postId) => {
    try {
      await axios.delete(`http://localhost:5000/draftsview/${postId}`);
      setDrafts((prevDrafts) => prevDrafts.filter((draft) => draft._id !== postId));
      alert("Draft deleted successfully!");
    } catch (error) {
      console.error("Error deleting draft:", error);
      alert("Failed to delete draft.");
    }
  };

  const handleView = (draft) => {
    navigate("/newpost", { state: { draft } });
  };

  return (
    <div className="drafts-container">
      <h1>Drafts</h1>
      {drafts.length > 0 ? (
        drafts.map((draft) => (
          <div key={draft._id} className="draft-card">
            <h2>{draft.title}</h2>
            <p>{draft.description}</p>
            <div className="media-container">
              {draft.media.map((file, index) => {
                if (file.type.startsWith("image/")) {
                  return (
                    <img key={index} src={file.url} alt="Draft media" className="media-item" />
                  );
                } else if (file.type === "application/pdf") {
                  return (
                    <a key={index} href={file.url} target="_blank" rel="noopener noreferrer">
                      View PDF
                    </a>
                  );
                } else {
                  return (
                    <a key={index} href={file.url} target="_blank" rel="noopener noreferrer">
                      Download File
                    </a>
                  );
                }
              })}
            </div>
            <div className="button-group">
              <button className="view-button" onClick={() => handleView(draft)}>
                View
              </button>
              <button className="delete-button" onClick={() => handleDelete(draft._id)}>
                Delete
              </button>
            </div>
          </div>
        ))
      ) : (
        <p>No drafts available</p>
      )}
    </div>
  );
};

export default Drafts;
