import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosInstance"; // Ensure this points to your backend API
import "./Login.css";
import "font-awesome/css/font-awesome.min.css";  // Import FontAwesome icons

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false); // State to toggle password visibility
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const togglePasswordVisibility = () => {
    setShowPassword((prevState) => !prevState);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axiosInstance.post("/login", { email, password });
      const { role, redirectTo, email: userEmail } = response.data;

      // Save user data to localStorage for persistence
      localStorage.setItem("userRole", role);
      localStorage.setItem("userEmail", userEmail);

      // Trigger onLogin callback and navigate
      onLogin(role, userEmail);
      navigate(redirectTo);
    } catch (err) {
      console.error("Login error:", err);
      setError(err.response?.data?.message || "Invalid login credentials");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>}
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group password-input">
          <label>Password</label>
          <input
            type={showPassword ? "text" : "password"} // Toggle between "text" and "password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <span
            className="toggle-password"
            onClick={togglePasswordVisibility}
          >
            {showPassword ? (
              <i className="fa fa-eye-slash"></i> // FontAwesome eye-slash icon
            ) : (
              <i className="fa fa-eye"></i> // FontAwesome eye icon
            )}
          </span>
        </div>
        <button type="submit" className="login-button">
          Login
        </button>
      </form>
    </div>
  );
};

export default Login;
