import React, { useEffect, useState } from "react";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [activeCategory, setActiveCategory] = useState(null);
  const [showMore, setShowMore] = useState({});
  const [posts, setPosts] = useState([]); // State for storing fetched posts

  // Static categories data (can be fetched or modified based on requirements)
  const categories = [
    { name: "Club", subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "Happy Club"] },
    { name: "Academics", subcategories: ["First year", "Second year", "Third year", "Fourth year"] },
    { name: "Notices", subcategories: [] },
    { name: "Skill Hub", subcategories: [] },
    { name: "Events", subcategories: [] },
    { name: "Placements", subcategories: [] },
  ];

  // Fetch posts from the backend
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/upload"); // Replace with your server URL
        const data = await response.json();
        setPosts(data); // Set the fetched posts
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, []); // Empty dependency array ensures this runs once when the component mounts

  // Handle "Read More" and "Read Less" toggle
  const toggleShowMore = (index) => {
    setShowMore((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  // Handle category hover to display subcategories
  const handleCategoryHover = (categoryName) => {
    setActiveCategory(categoryName);
  };

  const handleCategoryLeave = () => {
    setActiveCategory(null);
  };

  return (
    <div>
      <h1>Dashboard</h1>

      {/* Categories Section */}
      <div style={{ display: "flex", gap: "20px", marginBottom: "20px" }}>
        {categories.map((category) => (
          <div
            key={category.name}
            className="category-wrapper"
            onMouseEnter={() => handleCategoryHover(category.name)}
            onMouseLeave={handleCategoryLeave}
            style={{ position: "relative", textAlign: "center" }}
          >
            <div className="category-icon">
              <img
                src={`https://via.placeholder.com/100?text=${category.name}`}
                alt={`${category.name} Icon`}
                className="category-image"
              />
            </div>
            <div className="category-name">{category.name}</div>

            {/* Subcategory Dropdown */}
            {category.subcategories.length > 0 && activeCategory === category.name && (
              <div className="sub-category-container">
                <ul>
                  {category.subcategories.map((subcategory, subIndex) => (
                    <li key={subIndex} className="subcategory-item">
                      {subcategory}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>

      {/* Feed Section */}
      <div style={{ marginTop: "20px" }}>
        <h2>Feed</h2>
        {posts.length > 0 ? (
          posts.map((post, index) => (
            <div
              key={index}
              style={{
                display: "flex",
                alignItems: "flex-start",
                marginBottom: "20px",
                border: "1px solid #ccc",
                padding: "10px",
              }}
            >
              <div style={{ width: "30%" }}>
                {post.images && post.images.length > 0 && (
                  <Carousel showThumbs={false} infiniteLoop={true} autoPlay={true} interval={3000}>
                    {post.images.map((image, imgIndex) => (
                      <div key={imgIndex}>
                        <img
                          src={image}
                          alt={`Slide ${index}-${imgIndex}`}
                          style={{ width: "100%", height: "auto" }}
                        />
                      </div>
                    ))}
                  </Carousel>
                )}
              </div>
              <div style={{ width: "70%", paddingLeft: "10px" }}>
                <h3>{post.title}</h3>
                <p>
                  {showMore[index] || post.text.length <= 100
                    ? post.text
                    : `${post.text.substring(0, 100)}...`}
                </p>
                {post.text.length > 100 && (
                  <button onClick={() => toggleShowMore(index)}>
                    {showMore[index] ? "Read Less" : "Read More"}
                  </button>
                )}
              </div>
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>
    </div>
  );
};

export default DashboardContent;
