import React, { useState, useEffect } from "react";

const Newpost = ({ userEmail, draftData }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [status, setStatus] = useState("draft");
  const [link, setLink] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    if (draftData) {
      setTitle(draftData.title || "");
      setDescription(draftData.description || "");
      setCategory(draftData.category || "");
      setSubCategory(draftData.subCategory || "");
      setStatus(draftData.status || "draft");
      setLink(draftData.link || "");
    }
  }, [draftData]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory || "");
    formData.append("status", status);
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link || "");

    if (selectedFile) {
      formData.append("file", selectedFile);
    }

    console.log("Submitting form data:", Object.fromEntries(formData.entries()));

    try {
      const response = await fetch(`http://localhost:3000/draftsview/update/${draftData?._id}`, {
        method: "PUT",
        body: formData,
      });

      const result = await response.json();
      if (!response.ok) throw new Error(result.error || "Failed to update draft");

      alert("Draft updated successfully!");
    } catch (error) {
      console.error("Error updating draft:", error);
      alert("Error updating draft.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} placeholder="Title" required />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description" required />
      <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} placeholder="Category" required />
      <input type="text" value={subCategory} onChange={(e) => setSubCategory(e.target.value)} placeholder="Sub-category" />
      <input type="text" value={link} onChange={(e) => setLink(e.target.value)} placeholder="Link" />
      <input type="file" onChange={handleFileChange} />
      <button type="submit">Update Draft</button>
    </form>
  );
};

export default Newpost;
