import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axiosInstance from "./api/axiosfetch";
import "./Postspage.css";

const Postspage = ({ userEmail }) => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    let email = userEmail || localStorage.getItem("userEmail");


    if (!email) {
      return;
    }

    const fetchPosts = async () => {
      try {
        
        const url = `/postview/${encodeURIComponent(email)}`;
        
    
        const response = await axiosInstance.get(url);
       
        setPosts(response.data);
      } catch (error) {
       
      }
    };

    fetchPosts();
  }, [userEmail]);

  return (
    <div className="posts-container">
      <h1>User Posts</h1>

      {/* New Post Button */}
      <button className="new-post-button" onClick={() => navigate("/Newpost")}>
         New Post
      </button>

      <div className="feed-section">
        <h2>Feed</h2>
        <div className="feed-grid">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts
              .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)) // Sort by newest first
              .map((post) => (
                <div key={post._id} className="feed-item">
                  {post.media.length > 0 && (
                    <img
                      className="feed-image-item"
                      src={post.media[0].url}
                      alt={post.title}
                    />
                  )}
                  <div className="feed-content">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                  </div>
                </div>
              ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Postspage;
