import React, { useState } from "react";
import axios from "axios";
import "./Upload.css";

const UploadImage = () => {
  const [image, setImage] = useState(null); // For storing the selected file
  const [uploadStatus, setUploadStatus] = useState(""); // For showing upload status

  // Handle file selection
  const handleFileChange = (event) => {
    setImage(event.target.files[0]);
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!image) {
      alert("Please select an image to upload.");
      return;
    }

    // Create a FormData object for the Cloudinary upload
    const formData = new FormData();
    formData.append("file", image);
    formData.append("upload_preset", "YOUR_UPLOAD_PRESET"); // Replace with your Cloudinary preset

    try {
      // Upload the image to Cloudinary
      const cloudinaryResponse = await axios.post(
        "https://api.cloudinary.com/v1_1/YOUR_CLOUD_NAME/image/upload",
        formData
      );

      const imageUrl = cloudinaryResponse.data.secure_url;

      // Save the Cloudinary image URL to MongoDB
      const serverResponse = await axios.post("http://localhost:3000/posts", {
        imageUrl, // Send the Cloudinary URL to the backend
      });

      setUploadStatus("Image uploaded successfully!");
      console.log("Server response:", serverResponse.data);
    } catch (error) {
      console.error("Error uploading image:", error);
      setUploadStatus("Failed to upload image.");
    }
  };

  return (
    <div className="upload-container">
      <h2>Upload Image</h2>
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      {uploadStatus && <p>{uploadStatus}</p>}
    </div>
  );
};

export default UploadImage;
