// NewPost.js
import React, { useState } from "react";
import axios from "axios";
import './NewPost.css';

const NewPost = () => {
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    if (files) {
      setFileNames(files.map((file) => file.name));
    }
  };

  const handlePost = async () => {
    console.log("Starting post submission...");

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);

    const files = document.getElementById("fileInput").files;
    console.log("Selected files:", files);

    if (!files.length) {
      alert("Please upload at least one file.");
      return;
    }

    Array.from(files).forEach((file) => {
      formData.append("media", file);
    });

    try {
      console.log("Submitting form data...");
      const response = await axios.post("http://localhost:3000/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log("Response from server:", response.data);

      alert("Post submitted successfully!");
    } catch (err) {
      console.error("Error during post submission:", err.response || err);
      alert("Failed to submit post!");
    }
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0 ? fileNames.join(", ") : "Click to browse or drop files here"}
          </label>
          <input type="file" id="fileInput" onChange={handleFileChange} multiple />
        </div>

        <input
          type="text"
          placeholder="Enter title for the file"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        <select value={category} onChange={(e) => setCategory(e.target.value)} required>
          <option value="" disabled>Select a category</option>
          <option value="category1">Category 1</option>
          <option value="category2">Category 2</option>
        </select>

        <select value={subCategory} onChange={(e) => setSubCategory(e.target.value)} required>
          <option value="" disabled>Select a subcategory</option>
          <option value="subcategory1">Subcategory 1</option>
          <option value="subcategory2">Subcategory 2</option>
        </select>

        <button type="button" onClick={handlePost}>Post</button>
      </form>
    </div>
  );
};

export default NewPost;
