import React, { useState, useEffect, useMemo } from "react";
import "./Newpost.css";
import { useLocation } from "react-router-dom";

const NewPost = ({ userEmail }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [files, setFiles] = useState([]);
  const [filePreviews, setFilePreviews] = useState([]);
  const [draftId, setDraftId] = useState(null);

  const location = useLocation();

  const draft = useMemo(() => location.state?.draft || {}, [location.state?.draft]);

  useEffect(() => {
    if (draft) {
      setTitle(draft.title || "");
      setDescription(draft.description || "");
      setCategory(draft.category || "");
      setSubCategory(draft.subCategory || "");
      setLink(draft.link || "");
      setFilePreviews(draft.media || []);
      setDraftId(draft._id || null); // Store draft ID if editing a draft
    }
  }, [draft]);

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);

    const previews = selectedFiles.map((file) => ({
      name: file.name,
      url: URL.createObjectURL(file),
    }));

    setFilePreviews((prevPreviews) => [...prevPreviews, ...previews]);
  };

  const handleFileRemove = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
    setFilePreviews((prevPreviews) => prevPreviews.filter((_, i) => i !== index));
  };

  const resetForm = () => {
    const confirmCancel = window.confirm("Are you sure you want to cancel? Your changes will be lost.");
    if (confirmCancel) {
      setTitle("");
      setDescription("");
      setCategory("");
      setSubCategory("");
      setLink("");
      setFiles([]);
      setFilePreviews([]);
      setDraftId(null);
    }
  };
  

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link);

    if (files.length > 0) {
      files.forEach((file) => {
        formData.append("media", file);
      });
    }

    try {
      const url = draftId
        ? `http://localhost:3000/posts/update-post/${draftId}`
        : "http://localhost:3000/posts/create-post";

      const method = draftId ? "PATCH" : "POST";

      const response = await fetch(url, {
        method,
        body: formData,
      });

      if (response.ok) {
        alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");
        resetForm();
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        {/* File Upload Section */}
        <div className="file-upload">
          <label htmlFor="fileInput">
            {files.length > 0 ? "Add more files" : "Click to browse or drop files here"}
          </label>
          <input type="file" id="fileInput" onChange={handleFileChange} multiple style={{ display: "none" }} />
        </div>

        {/* Display Uploaded Files */}
        <div className="uploaded-files">
          {filePreviews.map((file, index) => (
            <div key={index} className="file-item">
              <a href={file.url} target="_blank" rel="noopener noreferrer">{file.name}</a>
              <button type="button" className="remove-button" onClick={() => handleFileRemove(index)}>❌</button>
            </div>
          ))}
        </div>

        {/* Text Fields */}
        <input
          type="text"
          placeholder="Enter title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        {/* Link Field */}
        <input
          type="text"
          placeholder="Enter link (if any)"
          value={link}
          onChange={(e) => setLink(e.target.value)}
        />

        {/* Category Selection */}
        <select
          value={category}
          onChange={(e) => {
            setCategory(e.target.value);
            setSubCategory("");
          }}
          required
        >
          <option value="" disabled>
            Select a category
          </option>
          <option value="events">Events</option>
          <option value="clubs">Clubs</option>
          <option value="academics">Academics</option>
          <option value="notices">Notices</option>
          <option value="skillhub">Skillhub</option>
          <option value="placements">Placements</option>
        </select>

        {/* Subcategory Dropdown */}
        {category && subCategoriesMap[category]?.length > 0 && (
          <select value={subCategory} onChange={(e) => setSubCategory(e.target.value)}>
            <option value="" disabled>
              Select a subcategory
            </option>
            {subCategoriesMap[category].map((sub, index) => (
              <option key={index} value={sub.toLowerCase()}>
                {sub}
              </option>
            ))}
          </select>
        )}

        {/* Buttons */}
        <div className="button-container">
          <button type="button" onClick={() => handleSubmit(false)}>
            Submit Post
          </button>
          <button type="button" onClick={() => handleSubmit(true)}>
            Save as Draft
          </button>
          <button type="button" className="cancel-button" onClick={resetForm}>
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default NewPost;



