import React, { useState } from "react";
import axios from "axios";
import './NewPost.css';

const categories = [
  { name: "Club", subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "Happy Club"] },
  { name: "Academics", subcategories: ["First year", "Second year", "Third year", "Fourth year"] },
  { name: "Notices", subcategories: [] },
  { name: "Skill Hub", subcategories: ["Hackathons", "Coding contests", "Training Programs"] },
  { name: "Events", subcategories: [] },
  { name: "Placements", subcategories: [] },
];

const NewPost = () => {
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [uploaderEmail, setUploaderEmail] = useState("");
  const [files, setFiles] = useState([]);

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (selectedFiles) {
      setFileNames([...fileNames, ...selectedFiles.map((file) => file.name)]);
      setFiles([...files, ...selectedFiles]);
    }
  };

  const handleRemoveFile = (index) => {
    const updatedFiles = files.filter((_, i) => i !== index);
    setFiles(updatedFiles);
    setFileNames(updatedFiles.map((file) => file.name));
  };

  const handlePost = async () => {
    if (!title || !description || !category) {
      alert("Please fill in all required fields.");
      return;
    }
  
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("uploaderEmail", uploaderEmail);
  
    files.forEach((file) => formData.append("media", file));
  
    try {
      await axios.post("http://localhost:3000/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Post submitted successfully!");
      setTitle("");
      setDescription("");
      setCategory("");
      setSubCategory("");
      setUploaderEmail("");
      setFiles([]);
      setFileNames([]);
    } catch (error) {
      console.error(error);
      alert("Error uploading post.");
    }
  };
  
  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0 ? fileNames.join(", ") : "Click to browse or drop files here"}
          </label>
          <input type="file" id="fileInput" multiple onChange={handleFileChange} />
        </div>
        {fileNames.length > 0 && (
          <ul>
            {fileNames.map((file, index) => (
              <li key={index}>
                {file} <button type="button" onClick={() => handleRemoveFile(index)}>Remove</button>
              </li>
            ))}
          </ul>
        )}
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input
          type="email"
          placeholder="Uploader Email"
          value={uploaderEmail}
          onChange={(e) => setUploaderEmail(e.target.value)}
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="">Select Category</option>
          {categories.map((cat) => (
            <option key={cat.name} value={cat.name}>
              {cat.name}
            </option>
          ))}
        </select>
        {categories.find((cat) => cat.name === category)?.subcategories.length > 0 && (
          <select value={subCategory} onChange={(e) => setSubCategory(e.target.value)}>
            <option value="">Select Subcategory</option>
            {categories
              .find((cat) => cat.name === category)
              .subcategories.map((subcat) => (
                <option key={subcat} value={subcat}>
                  {subcat}
                </option>
              ))}
          </select>
        )}
        <button type="button" onClick={handlePost}>
          Submit
        </button>
      </form>
    </div>
  );
};

export default NewPost;
