import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./DashboardContent.css";

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [sortOrder, setSortOrder] = useState("desc"); // Default: Newest First
  const [activeCategory, setActiveCategory] = useState(null);
  const [showFilterOptions, setShowFilterOptions] = useState(false); // Toggle Filter Options
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch("http://localhost:3000/fetch/");
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, []);

  const filteredPosts = posts.filter(post => {
    const searchString = searchTerm.toLowerCase();
    return Object.values(post).some(value => {
      if (typeof value === "string") {
        return value.toLowerCase().includes(searchString);
      } else if (Array.isArray(value)) {
        return value.some(item => typeof item === "string" && item.toLowerCase().includes(searchString));
      }
      return false;
    });
  });

  const sortedPosts = filteredPosts.sort((a, b) => {
    return sortOrder === "desc"
      ? new Date(b.createdAt) - new Date(a.createdAt)
      : new Date(a.createdAt) - new Date(b.createdAt);
  });

  return (
    <div className="outer-box-dashboard">
      <div className="left-container-dashboard">
        <div className="feed-section-dashboard">
          <h2>Feed</h2>
          <div className="feed-grid-dashboard">
            {sortedPosts.map((post, index) => (
              <div className="feed-item-dashboard" key={index}>
                <div className="feed-image-dashboard">
                  {post.media.length > 0 && post.media[0].url && (
                    <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                      {post.media.map((media, idx) => (
                        <div key={idx}>
                          {media.type.includes("image") ? (
                            <img src={media.url} alt={post.title} style={{ objectFit: "cover", width: "100%" }} />
                          ) : media.type.includes("video") ? (
                            <video src={media.url} controls style={{ width: "100%" }} />
                          ) : null}
                        </div>
                      ))}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content-dashboard">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a href={post.link} target="_blank" rel="noopener noreferrer" className="post-link-dashboard">
                      {post.link}
                    </a>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Additional Feed Section */}
        <div className="feed-section-postpage-dashboard">
          <h2>Feed</h2>
          <div className="feed-grid-postpage-dashboard">
            {posts.length === 0 ? (
              <p>No posts available.</p>
            ) : (
              posts.map((post) => (
                <div className="feed-item-postpage-dashboard" key={post._id}>
                  <div className="feed-image-postpage-dashboard">
                    {post.media.length > 0 && post.media[0].url && (
                      <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                        {post.media.map((media, idx) => {
                          if (media.type.startsWith("image/")) {
                            return (
                              <div key={idx}>
                                <img src={media.url} alt={`${post.title} ${idx}`} style={{ objectFit: "cover", width: "100%" }} />
                              </div>
                            );
                          } else if (media.type === "application/pdf") {
                            return (
                              <div key={idx} className="pdf-container-postpage-dashboard">
                                <iframe src={`https://docs.google.com/gview?url=${media.url}&embedded=true`} title="PDF Viewer" width="100%" height="500px" style={{ border: "none" }}></iframe>
                              </div>
                            );
                          } else if (media.type === "video/mp4") {
                            return (
                              <div key={idx} className="video-container-postpage-dashboard">
                                <video src={media.url} controls style={{ width: "100%", height: "auto" }} />
                              </div>
                            );
                          }
                          return null;
                        })}
                      </Carousel>
                    )}
                  </div>
                  <div className="feed-content-postpage-dashboard">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    {post.link && (
                      <a href={post.link} target="_blank" rel="noopener noreferrer" className="post-link-postpage-dashboard">
                        {post.link}
                      </a>
                    )}
                  </div>
                  <button className="delete-post-button-postpage-dashboard" onClick={() => handleDelete(post._id)}>
                    Delete
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardContent;
