import React, { useState } from "react";
import axios from "axios";
import './NewPost.css';  // Ensure you add appropriate styles for the layout

const categories = [
  { name: "Club", subcategories: ["RMF", "Splash Out", "Rhythmic Thunders", "VPOD", "Happy Club"] },
  { name: "Academics", subcategories: ["First year", "Second year", "Third year", "Fourth year"] },
  { name: "Notices", subcategories: [] },
  { name: "Skill Hub", subcategories: ["Hackathons","Coding contests","Training Programs"] },
  { name: "Events", subcategories: [] },
  { name: "Placements", subcategories: [] },
];

const NewPost = () => {
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [uploaderEmail, setUploaderEmail] = useState("");
  const [files, setFiles] = useState([]);  // Track uploaded files

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    if (selectedFiles) {
      setFileNames([...fileNames, ...selectedFiles.map((file) => file.name)]);
      setFiles([...files, ...selectedFiles]);
    }
  };

  const handleCategoryChange = (e) => {
    const selectedCategory = e.target.value;
    setCategory(selectedCategory);

    // Reset the subCategory field when category changes
    const selectedCategoryObject = categories.find((cat) => cat.name === selectedCategory);
    if (!selectedCategoryObject?.subcategories.length) {
      setSubCategory("");
    }
  };

  const handleRemoveFile = (index) => {
    const newFileNames = [...fileNames];
    const newFiles = [...files];
    newFileNames.splice(index, 1);
    newFiles.splice(index, 1);
    setFileNames(newFileNames);
    setFiles(newFiles);
  };

  const handlePost = async () => {
    if (!title.trim() || !description.trim() || !category.trim()) {
      alert("Please fill out all required fields: title, description, and category.");
      return;
    }
  
    if (files.length === 0) {
      alert("Please upload at least one file.");
      return;
    }
  
    const formData = new FormData();
    formData.append("title", title.trim());
    formData.append("description", description.trim());
    formData.append("category", category);
    formData.append("subCategory", subCategory || ""); // Handle cases where subCategory might be empty
    formData.append("uploaderEmail", uploaderEmail.trim());
  
    // Append all selected files to the FormData
    files.forEach((file) => {
      formData.append("media", file);
    });
  
    try {
      const response = await axios.post("http://localhost:3000/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
  
      if (response.status === 201) {
        alert("Post submitted successfully!");
        // Optionally reset the form after a successful submission
        setTitle("");
        setDescription("");
        setCategory("");
        setSubCategory("");
        setUploaderEmail("");
        setFiles([]);
        setFileNames([]);
      } else {
        alert("Failed to submit post. Please try again.");
      }
    } catch (err) {
      console.error("Error during post submission:", err.response || err.message);
      alert(
        err.response?.data?.message || "An unexpected error occurred. Please try again."
      );
    }
  };
  

  const handleSaveToDrafts = async () => {
    const draftData = {
      title,
      description,
      category,
      subCategory,
      uploaderEmail,
      files: fileNames,
    };

    // Simulate saving draft (you could save this to a backend or local storage)
    console.log("Saving to drafts:", draftData);
    alert("Draft saved successfully!");
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        <div className="file-upload">
          <label htmlFor="fileInput">
            {fileNames.length > 0 ? fileNames.join(", ") : "Click to browse or drop files here"}
          </label>
          <input type="file" id="fileInput" onChange={handleFileChange} multiple />
        </div>

        <div className="uploaded-files">
          {fileNames.length > 0 && (
            <ul>
              {fileNames.map((fileName, index) => (
                <li key={index} className="file-item">
                  {fileName}
                  <span className="remove-icon" onClick={() => handleRemoveFile(index)}>Ã—</span>
                </li>
              ))}
            </ul>
          )}
        </div>

        <input
          type="text"
          placeholder="Enter title for the file"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />

        <textarea
          placeholder="Write a description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        ></textarea>

        <input
          type="email"
          placeholder="Enter your email"
          value={uploaderEmail}
          onChange={(e) => setUploaderEmail(e.target.value)}
          required
        />

        <select value={category} onChange={handleCategoryChange} required>
          <option value="" disabled>Select a category</option>
          {categories.map((cat) => (
            <option key={cat.name} value={cat.name}>
              {cat.name}
            </option>
          ))}
        </select>

        <select
          value={subCategory}
          onChange={(e) => setSubCategory(e.target.value)}
          disabled={!categories.find((cat) => cat.name === category)?.subcategories.length}
          required
        >
          <option value="" disabled>Select a subcategory</option>
          {categories
            .find((cat) => cat.name === category)
            ?.subcategories.map((subcat) => (
              <option key={subcat} value={subcat}>
                {subcat}
              </option>
            ))}
        </select>

        <div className="button-container">
          <button type="button" onClick={handlePost} className="post-btn">Post</button>
          <button type="button" onClick={handleSaveToDrafts} className="draft-btn">Save to Drafts</button>
        </div>
      </form>
    </div>
  );
};

export default NewPost;
