import React, { useState, useEffect } from "react";
import axios from "axios";
import "./DashboardContent.css"; // Assuming you have a CSS file for styling

const DashboardContent = () => {
  const [posts, setPosts] = useState([]);
  const [error, setError] = useState("");

  // Fetch posts on component mount
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        // Make a GET request to your backend API
        const response = await axios.get("/api/posts"); // Adjust the URL as needed
        setPosts(response.data); // Set posts data in state
      } catch (err) {
        console.error("Error fetching posts:", err);
        setError("Failed to load posts.");
      }
    };

    fetchPosts();
  }, []); // Empty dependency array ensures it runs once after the initial render

  return (
    <div className="dashboard-content">
      <h2>Dashboard</h2>
      {error && <p className="error-message">{error}</p>} {/* Show error if any */}

      {/* Render posts */}
      <div className="posts-container">
        {posts.length > 0 ? (
          posts.map((post) => (
            <div key={post._id} className="post">
              <h3>{post.title}</h3>
              <p>{post.description}</p>
              <div className="post-meta">
                <span>Posted by: {post.author}</span>
                <span>{new Date(post.date).toLocaleDateString()}</span>
              </div>
            </div>
          ))
        ) : (
          <p>No posts available.</p>
        )}
      </div>
    </div>
  );
};

export default DashboardContent;
