import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useNavigate,
  useLocation,
} from "react-router-dom";
import Login from "./Login";
import StudentDashboard from "./studentdashboard";
import CoordinatorDashboard from "./coordinatordashboard";
import StudentProfile from "./studentprofile";
import CoordinatorProfile from "./coordinatorprofile";
import Home from "./Home";
import DashboardContent from "./Dashboardcontent";
import Postspage from "./PostPage";
import Newpost from "./newpost";
import Header from "./Header";
import DraftsPage from "./DraftsPage";

const App = () => {
  const [userRole, setUserRole] = useState(null);
  const [userEmail, setUserEmail] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    // Load user data from localStorage on mount
    const storedRole = localStorage.getItem("userRole");
    const storedEmail = localStorage.getItem("userEmail");

    if (storedRole && storedEmail) {
      setUserRole(storedRole);
      setUserEmail(storedEmail);
    }
  }, []);

  const handleLogin = (role, email) => {
    setUserRole(role);
    setUserEmail(email);

    // Redirect based on role
    if (role === "student") {
      navigate("/studentdashboard");
    } else if (role === "coordinator") {
      navigate("/coordinatordashboard");
    }
  };

  const handleLogout = () => {
    localStorage.clear(); // Clear localStorage on logout
    setUserRole(null);
    setUserEmail(null);
    navigate("/login");
  };

  // Exclude Header for specific routes
  const excludeHeaderRoutes = ["/", "/login"];

  return (
    <>
      {userRole &&
        !excludeHeaderRoutes.includes(location.pathname.toLowerCase()) && (
          <Header userRole={userRole} userEmail={userEmail} onLogout={handleLogout} />
        )}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <Route path="/studentdashboard" element={<StudentDashboard />} />
        <Route path="/coordinatordashboard" element={<CoordinatorDashboard />} />
        <Route path="/profile/student" element={<StudentProfile />} />
        <Route path="/profile/coordinator" element={<CoordinatorProfile />} />
        <Route path="/dashboardcontent" element={<DashboardContent />} />
        <Route path="/postspage" element={<Postspage />} />
        <Route path="/newpost" element={<Newpost />} />
        <Route path="/draftspage" element={<DraftsPage />} />
        <Route path="*" element={<div>404 - Page Not Found</div>} />
      </Routes>
    </>
  );
};

const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
