import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // For navigation
import "./Login.css"; // Import the CSS file

const Login = () => {
  const navigate = useNavigate();

  // Hardcoded email-password pairs
  const credentials = {
    "user1@example.com": {
      password: "password123",
      redirectTo: "/studentdashboard",
    },
    "user2@example.com": {
      password: "password456",
      redirectTo: "/coordinatordashboard",
    },
  };

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (credentials[email] && credentials[email].password === password) {
      // Redirect to the specific dashboard based on the email
      navigate(credentials[email].redirectTo);
    } else {
      setError("Invalid email or password");
    }
  };

  return (
    <div className="login-container">
      <form className="login-form" onSubmit={handleSubmit}>
        <h2>Login</h2>
        {error && <p className="error">{error}</p>} {/* Display error message */}
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            id="email"
            type="text"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            id="password"
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="login-button">
          LOGIN
        </button>
      </form>
    </div>
  );
};

export default Login;
