import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "./RMFpage.css";

const subCategoriesMap = {
  clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
  academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
  skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
};

const categories = [
  {
    name: "Events",
    value: "events",
    image: "/images/events-icon.jpg",
  },
  {
    name: "Clubs",
    value: "clubs",
    image: "/images/clubs-image.jpg",
  },
  {
    name: "Academics",
    value: "academics",
    image: "/images/academic-excellence.jpg",
  },
  {
    name: "Notices",
    value: "notices",
    image: "/images/notice.jpg",
  },
  {
    name: "Skill Hub",
    value: "skillhub",
    image: "/images/skill-hub.jpg",
  },
  {
    name: "Placements",
    value: "placements",
    image: "/images/placements.jpg",
  },
];

const rmf = () => {
  const [posts, setPosts] = useState([]);
  const [activeCategory, setActiveCategory] = useState("");
  const [activeSubCategory, setActiveSubCategory] = useState("");

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const categoryQuery = activeCategory ? `?category=${activeCategory}` : "";
        const subCategoryQuery = activeSubCategory ? `&subcategory=${activeSubCategory}` : "";
        const response = await fetch(`http://localhost:3000/posts${categoryQuery}${subCategoryQuery}`);
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };
    fetchPosts();
  }, [activeCategory, activeSubCategory]);

  return (
    <div className="outer-box">
      {/* Left Sidebar */}
      <div className="left-container">
        <h1>Dashboard</h1>
        <div className="category-container">
          {categories.map((category) => (
            <div
              key={category.value}
              className="category-wrapper"
              onClick={() => {
                setActiveCategory(category.value);
                setActiveSubCategory("");
              }}
            >
              <div className="category-icon">
                <img src={category.image} alt={`${category.name} Icon`} className="category-image" />
              </div>
              <div className="category-name">{category.name}</div>
            </div>
          ))}
        </div>
        {/* Subcategories */}
        {subCategoriesMap[activeCategory] && (
          <div className="subcategory-container">
            {subCategoriesMap[activeCategory].map((subCategory) => (
              <button
                key={subCategory}
                className={`subcategory-item ${activeSubCategory === subCategory ? "active" : ""}`}
                onClick={() => setActiveSubCategory(subCategory)}
              >
                {subCategory}
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Feed Section */}
      <div className="feed-section-postpage">
        <h2>Feed</h2>
        <div className="feed-grid-postpage">
          {posts.length === 0 ? (
            <p>No posts available.</p>
          ) : (
            posts.map((post) => (
              <div className="feed-item-postpage" key={post._id}>
                <div className="feed-image-postpage">
                  {post.media.length > 0 && post.media[0].url && (
                    <Carousel showThumbs={false} infiniteLoop autoPlay interval={5000} showStatus={false}>
                      {post.media.map((media, idx) => {
                        if (media.type.startsWith("image/")) {
                          return <img key={idx} src={media.url} alt={`${post.title}`} />;
                        } else if (media.type === "application/pdf") {
                          return (
                            <iframe
                              key={idx}
                              src={`https://docs.google.com/gview?url=${media.url}&embedded=true`}
                              title="PDF Viewer"
                              width="100%"
                              height="500px"
                              style={{ border: "none" }}
                            ></iframe>
                          );
                        } else if (media.type === "video/mp4") {
                          return <video key={idx} src={media.url} controls style={{ width: "100%" }} />;
                        }
                        return null;
                      })}
                    </Carousel>
                  )}
                </div>
                <div className="feed-content-postpage">
                  <h3>{post.title}</h3>
                  <p>{post.description}</p>
                  {post.link && (
                    <a href={post.link} target="_blank" rel="noopener noreferrer">
                      {post.link}
                    </a>
                  )}
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default rmf;
