import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "./Newpost.css";

const NewPost = ({ userEmail }) => {
  const [draftId, setDraftId] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [files, setFiles] = useState([]);
  const [existingMedia, setExistingMedia] = useState([]);
  const location = useLocation();

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const draftIdFromParams = queryParams.get("draftId");

    if (draftIdFromParams) {
      console.log("Draft ID detected in URL:", draftIdFromParams);
      setDraftId(draftIdFromParams);
      fetchDraftData(draftIdFromParams);
    }
  }, [location.search]);

  const fetchDraftData = async (id) => {
    try {
      console.log("Fetching draft data for ID:", id);
      const response = await fetch(`http://localhost:3000/draftsview/edit/${id}`);
      if (!response.ok) throw new Error("Failed to fetch draft");

      const draft = await response.json();
      console.log("Draft data received:", draft);
      setTitle(draft.title);
      setDescription(draft.description);
      setCategory(draft.category);
      setSubCategory(draft.subCategory || "");
      setLink(draft.link || "");
      setExistingMedia(draft.media || []);
    } catch (error) {
      console.error("Error fetching draft:", error);
      alert("Could not load draft. Please try again.");
    }
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    console.log("Files selected:", selectedFiles);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);
  };

  const resetForm = () => {
    console.log("Resetting form");
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setLink("");
    setFiles([]);
    setExistingMedia([]);
    setDraftId(null);
  };

  const handleSubmit = async (isDraft = false) => {
    console.log("Submitting post. Is draft:", isDraft);
    if (!title || !description || !category) {
      alert("Please fill all required fields!");
      return;
    }
    
    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    console.log("Form values:", { title, description, category, subCategory, link, userEmail, files });
    
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link);
    
    files.forEach((file) => formData.append("media", file));
    
    try {
      let url, method;

      if (draftId) {
        console.log("Updating existing draft ID:", draftId);
        url = `http://localhost:3000/draftsview/update/${draftId}`;
        method = "PUT";
      } else {
        console.log("Creating new post");
        url = "http://localhost:3000/posts/create-post";
        method = "POST";
      }

      console.log("Sending request to:", url, "Method:", method);
      
      const response = await fetch(url, {
        method: method,
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Unknown error");
      }

      console.log("Post submitted successfully");
      alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");

      if (!isDraft && draftId) {
        console.log("Deleting draft after posting:", draftId);
        await fetch(`http://localhost:3000/draftsview/${draftId}`, { method: "DELETE" });
      }
      
      resetForm();
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Submission failed. Please try again.");
    }
  };

  return (
    <div className="container">
      <h2>{draftId ? "Edit Draft" : "New Post"}</h2>
      <iframe 
        src="your-source-url" 
        title="Embedded Content" 
        width="100%" 
        height="500px" 
        frameBorder="0" 
        allowFullScreen
      ></iframe>
    </div>
  );
};

export default NewPost;
