import React, { useEffect, useState } from "react";
import "./StudentProfile.css";

const StudentProfile = ({ email }) => {
  const [profileData, setProfileData] = useState(null);
  const [profileImage, setProfileImage] = useState(
    "https://via.placeholder.com/100" // Default placeholder image
  );
  const [showOptions, setShowOptions] = useState(false);
  const [loading, setLoading] = useState(true);  // For loading state

  // Fetch student details
  useEffect(() => {
    const fetchStudentDetails = async () => {
      setLoading(true);
      try {
        const response = await fetch(`/api/student/${email}`);
        console.log("Fetching student data for email:", email);
        if (response.ok) {
          const data = await response.json();
          console.log("Student Data:", data);  // Log the data to check the structure
          setProfileData(data);
  
          // Handle profile image if available
          if (data.image) {
            const blob = new Blob([new Uint8Array(data.image.data)], {
              type: "image/png",
            });
            const imageUrl = URL.createObjectURL(blob);
            setProfileImage(imageUrl);
          }
        } else {
          console.error("Error fetching student details");
        }
      } catch (error) {
        console.error("Error:", error);
      } finally {
        setLoading(false);
      }
    };
  
    fetchStudentDetails();
  }, [email]);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setProfileImage(reader.result);
        // Upload the image to the server if needed (this can be added later)
      };
      reader.readAsDataURL(file);
    }
    setShowOptions(false);
  };

  return (
    <div className="profile-container">
      {loading ? (
        <div>Loading...</div> // You can replace this with a loading spinner
      ) : (
        <div className="profile-card">
          <div className="profile-image-container">
            <img src={profileImage} alt="Profile" className="profile-image" />
            <button
              onClick={() => setShowOptions(!showOptions)}
              className="camera-icon-button"
            >
              <img
                src="/images/camera.jpg" // Add the camera icon image to your project folder
                alt="Camera Icon"
                className="camera-icon"
              />
            </button>
          </div>
          <h2 className="profile-username">{profileData?.name || "Username"}</h2>
          <p className="profile-role">Student</p>
          <div className="profile-details">
            <p>
              <strong>Name:</strong> {profileData?.name || "N/A"}
            </p>
            <p>
              <strong>Email:</strong> {profileData?.email || "N/A"}
            </p>
            <p>
              <strong>Registration No:</strong> {profileData?.studentid || "N/A"}
            </p>
            <p>
              <strong>Department:</strong> {profileData?.branch || "N/A"}
            </p>
            <p>
              <strong>Phone Number:</strong> {profileData?.phoneno || "N/A"}
            </p>
          </div>
          {showOptions && (
            <div className="options-menu">
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                className="upload-button"
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default StudentProfile;
