import React, { useState, useEffect } from "react";

const DashboardContent = () => {
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [selectedSubCategory, setSelectedSubCategory] = useState(null);
  const [showMore, setShowMore] = useState({});
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const feedData = [
    {
      images: [
        // "https://via.placeholder.com/150",
        // "https://via.placeholder.com/150/0000FF",
        "college_activity\public\images\background_login.jpg",
        "college_activity\public\images\background_login.jpg",
      ],
      text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec a nisi vitae augue vehicula viverra.",
    },
    {
      images: ["college_activity\public\images\background_login.jpg"],
      text: "Short content text.",
    },
  ];

  const handleCategoryClick = (category) => {
    setSelectedCategory(category);
    setSelectedSubCategory(null);
  };

  const handleSubCategoryChange = (e) => {
    setSelectedSubCategory(e.target.value);
  };

  const toggleShowMore = (index) => {
    setShowMore((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  // Auto-slide image logic
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % feedData.length);
    }, 3000); // Slide every 3 seconds

    return () => clearInterval(interval);
  }, [feedData.length]);

  return (
    <div>
      <h1>Dashboard</h1>
      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        <button onClick={() => handleCategoryClick("Category1")}>Category 1</button>
        <button onClick={() => handleCategoryClick("Category2")}>Category 2</button>
        <button>Button 3</button>
        <button>Button 4</button>
        <button>Button 5</button>
        <button>Button 6</button>
      </div>

      {selectedCategory && (
        <div>
          <label>
            Select Subcategory:
            <select onChange={handleSubCategoryChange}>
              <option value="">--Select--</option>
              <option value="Subcategory1">Subcategory 1</option>
              <option value="Subcategory2">Subcategory 2</option>
            </select>
          </label>
          {selectedSubCategory && <p>Selected: {selectedSubCategory}</p>}
        </div>
      )}

      <div style={{ marginTop: "20px" }}>
        <h2>Feed</h2>
        {feedData.map((item, index) => (
          <div
            key={index}
            style={{
              display: "flex",
              alignItems: "flex-start",
              marginBottom: "20px",
              border: "1px solid #ccc",
              padding: "10px",
            }}
          >
            <div style={{ width: "30%" }}>
              {item.images.length > 1 ? (
                <img
                  src={item.images[currentImageIndex]}
                  alt={`Slide ${index}`}
                  style={{ width: "100%", height: "auto" }}
                />
              ) : (
                <img
                  src={item.images[0]}
                  alt={`Single Image ${index}`}
                  style={{ width: "100%", height: "auto" }}
                />
              )}
            </div>
            <div style={{ width: "70%", paddingLeft: "10px" }}>
              <p>
                {showMore[index] || item.text.length <= 100
                  ? item.text
                  : `${item.text.substring(0, 100)}...`}
              </p>
              {item.text.length > 100 && (
                <button onClick={() => toggleShowMore(index)}>
                  {showMore[index] ? "Read Less" : "Read More"}
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardContent;
