  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState("");
  const [files, setFiles] = useState([]);
  const [filePreviews, setFilePreviews] = useState([]);
  const [existingMedia, setExistingMedia] = useState([]); // Stores existing media

  const location = useLocation();
  const navigate = useNavigate();

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  // Fetch draft data if editing an existing draft
  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const draftIdFromParams = queryParams.get("draftId");

    if (draftIdFromParams) {
      setDraftId(draftIdFromParams);
      fetchDraftData(draftIdFromParams);
    }
  }, [location.search]);

  const fetchDraftData = async (id) => {
    try {
      const response = await fetch(`http://localhost:3000/drafts/${id}`);
      if (response.ok) {
        const draft = await response.json();
        setTitle(draft.title);
        setDescription(draft.description);
        setCategory(draft.category);
        setSubCategory(draft.subCategory || "");
        setLink(draft.link || "");
        setExistingMedia(draft.media || []);
      } else {
        console.error("Failed to fetch draft");
      }
    } catch (error) {
      console.error("Error fetching draft:", error);
    }
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);

    const previews = selectedFiles.map((file) => ({
      name: file.name,
      url: URL.createObjectURL(file),
    }));

    setFilePreviews((prevPreviews) => [...prevPreviews, ...previews]);
  };

  const handleFileRemove = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
    setFilePreviews((prevPreviews) => prevPreviews.filter((_, i) => i !== index));
  };

  const handleExistingMediaRemove = (index) => {
    setExistingMedia((prevMedia) => prevMedia.filter((_, i) => i !== index));
  };

  const resetForm = () => {
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setLink("");
    setFiles([]);
    setFilePreviews([]);
    setExistingMedia([]);
    setDraftId(null);
    navigate("/drafts");
  };

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link);

    if (files.length > 0) {
      files.forEach((file) => {
        formData.append("media", file);
      });
    }

    if (draftId) {
      formData.append("draftId", draftId);
    }

    try {
      const response = await fetch("http://localhost:3000/posts/create-post", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");
        resetForm();
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  const handleCancel = async () => {
    if (!draftId) {
      resetForm();
      return;
    }

    const confirmDelete = window.confirm("Are you sure you want to delete this draft?");
    if (!confirmDelete) return;

    try {
      const response = await fetch(`http://localhost:3000/drafts/${draftId}`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Draft deleted successfully!");
        resetForm();
      } else {
        alert("Error deleting draft.");
      }
    } catch (error) {
      console.error("Error deleting draft:", error);
    }
  };

  return (
    <div className="container">
      <h2>{draftId ? "Edit Draft" : "New Post"}</h2>
      <form>
        {/* File Upload Section */}
        <div className="file-upload">
          <label htmlFor="fileInput">
            {files.length > 0 ? "Add more files" : "Click to browse or drop files here"}
          </label>
          <input type="file" id="fileInput" onChange={handleFileChange} multiple style={{ display: "none" }} />
        </div>

        {/* Display Existing & Uploaded Files */}
        <div className="uploaded-files">
          {existingMedia.map((media, index) => (
            <div key={index} className="file-item">
              <a href={media.url} target="_blank" rel="noopener noreferrer">{media.url.split("/").pop()}</a>
              <button type="button" className="remove-button" onClick={() => handleExistingMediaRemove(index)}>‚ùå</button>
            </div>
          ))}
          {filePreviews.map((file, index) => (
            <div key={index} className="file-item">
              <a href={file.url} target="_blank" rel="noopener noreferrer">{file.name}</a>
              <button type="button" className="remove-button" onClick={() => handleFileRemove(index)}>‚ùå</button>
            </div>
          ))}
        </div>

        {/* Text Fields */}
        <input type="text" placeholder="Enter title" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea placeholder="Write a description" value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <input type="text" placeholder="Enter link (if any)" value={link} onChange={(e) => setLink(e.target.value)} />

        {/* Category & Subcategory Selection */}
        <select value={category} onChange={(e) => { setCategory(e.target.value); setSubCategory(""); }} required>
          <option value="" disabled>Select a category</option>
          {Object.keys(subCategoriesMap).map((key) => <option key={key} value={key}>{key}</option>)}
        </select>
        {category && subCategoriesMap[category]?.length > 0 && (
          <select value={subCategory} onChange={(e) => setSubCategory(e.target.value)}>
            <option value="" disabled>Select a subcategory</option>
            {subCategoriesMap[category].map((sub, index) => <option key={index} value={sub.toLowerCase()}>{sub}</option>)}
          </select>
        )}

        {/* Buttons */}
        <div className="button-container">
          <button type="button" onClick={() => handleSubmit(false)}>Submit Post</button>
          <button type="button" onClick={() => handleSubmit(true)}>Save as Draft</button>
          <button type="button" className="cancel-button" onClick={handleCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default NewPost;
