import React, { useState } from "react";
import "./Newpost.css";

const NewPost = ({ userEmail }) => {
  const [fileNames, setFileNames] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [category, setCategory] = useState("");
  const [subCategory, setSubCategory] = useState("");
  const [link, setLink] = useState(""); 
  const [files, setFiles] = useState([]);
  const [showCancelDialog, setShowCancelDialog] = useState(false);

  const subCategoriesMap = {
    clubs: ["RMF", "Splashout", "VPOD", "Rhythmic Thunders", "Happy Club"],
    academics: ["First Year", "Second Year", "Third Year", "Fourth Year"],
    skillhub: ["Hackathons", "Coding Contests", "Training Programs"],
  };

  const handleFileChange = (event) => {
    const selectedFiles = Array.from(event.target.files);
    setFiles((prevFiles) => [...prevFiles, ...selectedFiles]);
    setFileNames((prevNames) => [...prevNames, ...selectedFiles.map((file) => file.name)]);
  };

  const handleFileRemove = (index) => {
    setFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
    setFileNames((prevNames) => prevNames.filter((_, i) => i !== index));
  };

  const resetForm = () => {
    setFileNames([]);
    setTitle("");
    setDescription("");
    setCategory("");
    setSubCategory("");
    setLink("");
    setFiles([]);
  };

  const handleSubmit = async (isDraft = false) => {
    if (!title || !description || !category) {
      alert("Please fill all the required fields!");
      return;
    }

    if (!userEmail) {
      alert("Error: User email is missing!");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("category", category);
    formData.append("subCategory", subCategory);
    formData.append("status", isDraft ? "draft" : "posted");  
    formData.append("uploaderEmail", userEmail);
    formData.append("link", link);

    files.forEach((file) => formData.append("media", file));

    try {
      const response = await fetch("http://localhost:3000/posts/create-post", {
        method: "POST",
        body: formData,
      });

      if (response.ok) {
        alert(isDraft ? "Draft saved successfully!" : "Post submitted successfully!");
        resetForm();
      } else {
        const error = await response.json();
        alert(`Error: ${error.error}`);
      }
    } catch (error) {
      console.error("Error submitting post:", error);
      alert("Failed to submit post. Please try again later.");
    }
  };

  const renderMediaPreview = (file) => {
    const fileType = file.type.split("/")[0];

    if (fileType === "image") {
      return <img src={file.url} alt="Uploaded Media" className="media-preview" />;
    } else if (fileType === "video") {
      return <video controls className="media-preview"><source src={file.url} type="video/mp4" /></video>;
    } else if (fileType === "audio") {
      return <audio controls><source src={file.url} type="audio/mpeg" /></audio>;
    } else if (file.type === "application/pdf") {
      return <embed src={file.url} type="application/pdf" width="100%" height="500px" />;
    } else {
      return <p>Unsupported file format</p>;
    }
  };

  return (
    <div className="container">
      <h2>New Post</h2>
      <form>
        <input type="file" onChange={handleFileChange} multiple />
        {files.map((file, index) => (
          <div key={index} className="file-item">
            {renderMediaPreview(file)}
            <button type="button" onClick={() => handleFileRemove(index)}>‚ùå</button>
          </div>
        ))}
        <button type="button" onClick={() => handleSubmit(false)}>Submit Post</button>
      </form>
    </div>
  );
};

export default NewPost;
