import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "./contextauth"; // Import AuthContext
import axios from "axios";
import "./Postspage.css";

const Postspage = () => {
  const { userEmail } = useContext(AuthContext); // Access the userEmail from AuthContext
  const [posts, setPosts] = useState([]);

  // Fetch posts when userEmail is available
  useEffect(() => {
    const fetchPosts = async () => {
      if (!userEmail) {
        console.log("User email is not available, skipping fetch.");
        return;
      }

      console.log("Fetching posts for user:", userEmail);

      try {
        const response = await axios.get(`http://localhost:3000/postview/posted?email=${userEmail}`);
        console.log("Posts fetched from backend:", response.data);
        setPosts(response.data);
      } catch (error) {
        console.error("Error fetching posts:", error);
      }
    };

    fetchPosts();
  }, [userEmail]);

  return (
    <div className="posts-container">
      <h1>User Posts</h1>
      {posts.length === 0 ? (
        <p>No posts available.</p>
      ) : (
        posts.map((post) => (
          <div key={post._id} className="post-card">
            <h3>{post.title}</h3>
            <p>{post.description}</p>
            {/* Add more post details here */}
          </div>
        ))
      )}
    </div>
  );
};

export default Postspage;
